import React from "react";
import * as WebBrowser from "expo-web-browser";
import {
  AuthRequest,
  exchangeCodeAsync,
  Prompt,
  useAutoDiscovery,
} from "expo-auth-session";
import { Button } from "react-native";

WebBrowser.maybeCompleteAuthSession();

export default function AuthorizationButton() {
  // Endpoint
  const discovery = useAutoDiscovery(
    "https://login.microsoftonline.com/65fa44cb-fe68-4b06-ad9f-d4343bd7589f/v2.0"
  );

  const authRequest = new AuthRequest({
    clientId: "a67a4317-87b9-403b-8db9-e0227117bc8a",
    scopes: ["openid", "profile", "email", "offline_access"],
    // For usage in managed apps using the proxy
    redirectUri: "http://localhost:19006/one",
    prompt: Prompt.Login,
    /* redirectUri: makeRedirectUri({
        // For usage in bare and standalone
        native: 'houe-plastic-recycling://react-native-auth/',
      }),
       */
  });

  // Request
  const onPress = async () => {
    if (discovery) {
      /* We don't care about the return value of this, but one of the side effects of it is that
       * the challenge and verifier is set up correctly. This is not the most elegant way of doing
       * it but that is the way the library works for now */
      await authRequest.getAuthRequestConfigAsync();
      const authSessionResult = await authRequest.promptAsync(discovery);
      console.log(authSessionResult);
      if (
        authSessionResult &&
        authSessionResult.type === "success" &&
        authRequest.codeVerifier &&
        discovery
      ) {
        exchangeCodeAsync(
          {
            code: authSessionResult.params.code,
            scopes: ["openid", "profile", "email", "offline_access"],
            clientId: "a67a4317-87b9-403b-8db9-e0227117bc8a",
            redirectUri: "http://localhost:19006/one",
            extraParams: {
              code_verifier: authRequest.codeVerifier,
            },
          },
          discovery
        )
          .then((tokenResponse) => {
            // TODO: In time, move this to an Azure function. For now, it is fine here
            const parts = tokenResponse.accessToken
              .split(".")
              .map((part) =>
                Buffer.from(
                  part.replace(/-/g, "+").replace(/_/g, "/"),
                  "base64"
                ).toString()
              );

            const payload = JSON.parse(parts[1]);

            console.log("JWT payload", payload);
          })
          .catch((error) => {
            console.log("ERROR: ");
            console.log(error);
          });
      }
    }
  };

  return <Button title="Login test" onPress={onPress} />;
}
